mapper.update:
UPDATE
 T_BEDU_QS SET TITLE = #{title}, CONTENT = #{content}, REG_DATE = SYSDATE()
WHERE QS_BD_NUM = #{qnaBdNum}

UPDATE
 T_BEDU_QS SET QS_CNT = QS_CNT + 1
WHERE QS_BD_NUM = #{num}

UPDATE
 T_BEDU_QS SET QS_LIKE_CNT = QS_LIKE_CNT + 1
WHERE QS_BD_NUM = #{num}

UPDATE
 T_BEDU_QS SET QS_LIKE_CNT = QS_LIKE_CNT - 1
WHERE QS_BD_NUM = #{num}
