mapper.update:
UPDATE
 T_BEDU_QS SET TITLE = #{title}, CONTENT = #{content}
WHERE QS_BD_NUM = #{qna_bd_num}

UPDATE
 T_BEDU_QS SET QS_CNT = QS_CNT + 1
WHERE QS_BD_NUM = #{num}

UPDATE
 T_BEDU_QS SET QS_LIKE_CNT = QS_LIKE_CNT + 1
WHERE QS_BD_NUM = #{num}

UPDATE
 T_BEDU_QS SET QS_LIKE_CNT = QS_LIKE_CNT - 1
WHERE QS_BD_NUM = #{num}
